export default function Courses() {
  // UI-состояния
  const [search, setSearch] = useState("");
  const [categoryFilter, setCategoryFilter] = useState([]);
  const [levelFilter, setLevelFilter] = useState([]);
  const [sortBy, setSortBy] = useState("rating");
  const [visibleCount, setVisibleCount] = useState(6);
  const pageSize = 6;

  // Доступные опции
  const allCategories = ["Maps", "Weapons", "Strategy"];
  const allLevels = ["Beginner", "Intermediate", "Advanced"];

  // Фильтрация + поиск + сортировка
  const filtered = useMemo(() => {
    let arr = sampleCourses;

    if (search) {
      const q = search.toLowerCase();
      arr = arr.filter(
        c =>
          c.title.toLowerCase().includes(q) ||
          c.description.toLowerCase().includes(q)
      );
    }

    if (categoryFilter.length) {
      arr = arr.filter(c => categoryFilter.includes(c.category));
    }

    if (levelFilter.length) {
      arr = arr.filter(c => levelFilter.includes(c.level));
    }

    arr = arr.sort((a, b) => (b[sortBy] || 0) - (a[sortBy] || 0));
    return arr;
  }, [search, categoryFilter, levelFilter, sortBy]);

  // Подгрузить ещё
  const loadMore = () =>
    setVisibleCount(prev => Math.min(prev + pageSize, filtered.length));

  // Тоггл чекбоксов
  const toggle = (val, list, setList) => {
    setList(prev =>
      prev.includes(val) ? prev.filter(i => i !== val) : [...prev, val]
    );
  };

  return (
    <div className="courses-page">
      <aside className="courses-filters">
        <h2>Фильтры</h2>
        <input
          type="text"
          placeholder="Поиск по курсам…"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />

        <div>
          <h3>Категории</h3>
          {allCategories.map(cat => (
            <label key={cat}>
              <input
                type="checkbox"
                checked={categoryFilter.includes(cat)}
                onChange={() => toggle(cat, categoryFilter, setCategoryFilter)}
              />
              {cat}
            </label>
          ))}
        </div>

        <div>
          <h3>Уровень</h3>
          {allLevels.map(lvl => (
            <label key={lvl}>
              <input
                type="checkbox"
                checked={levelFilter.includes(lvl)}
                onChange={() => toggle(lvl, levelFilter, setLevelFilter)}
              />
              {lvl}
            </label>
          ))}
        </div>

        <div>
          <h3>Сортировать по</h3>
          <select
            value={sortBy}
            onChange={e => setSortBy(e.target.value)}
          >
            <option value="rating">Рейтинг</option>
            <option value="isNew">Новизне</option>
          </select>
        </div>
      </aside>

      <main className="courses-main">
        <div className="courses-list">
          {filtered.slice(0, visibleCount).map(course => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>

        {visibleCount < filtered.length && (
          <button className="btn-load-more" onClick={loadMore}>
            Показать ещё
          </button>
        )}
      </main>
    </div>
  );
}